sys_variable_value.value:
(function execute(inputs, outputs) {
    try {
        var tbl = inputs.t;
        var fld = inputs.f;
        var sid = inputs.s;
        var fields = fld.split(",");
        var outObj = {};
        var grObj = new GlideRecord(tbl);
        grObj.get(sid);
        if (grObj.isValidRecord()) {
            var arr = [];
            outObj.result = "success";
            outObj.resultMsg = "Valid record with sys_id " + sid;
            for (var i = 0; i < fields.length; i++) {
                var field = fields[i];
                var fieldObj = {};
                fieldObj.field_name = field;
                if (grObj.isValidField(field)) {
                    fieldObj.field_label = grObj.getElement(field).getED().getLabel();
                    fieldObj.field_result = "success";
                    fieldObj.value = grObj.getValue(field);
                    fieldObj.displayValue = grObj.getDisplayValue(field);

                } else {
                    fieldObj.field_result = "error";
                    fieldObj.field_result_msg = field + " is an invalid field";
                }
               outObj[field]=fieldObj;
            }
            
        } else {
            outObj.result = "error";
            outObj.resultMsg = "Invalid record with sys_id " + sid;
        }
    } catch (e) {
        outObj.result = "error";
        outObj.resultMsg = gs.getMessage(e);
    }


    outputs.outstring = JSON.stringify(outObj);
})(inputs, outputs);

